INSERT INTO `field_gallery_image` (`pages_id`, `data`, `sort`, `description`, `modified`, `created`) VALUES('1105', '7.jpg', '7', '[\"Standardisierte Schulen: Im letzten Jahrzehnt sind in Äthiopien viele neue Schulen hinzugekommen. Jedoch sind in den meisten Dörfern Bruchbuden, die keine geeignete Lernatmosphäre bieten. Wir sehen es als unsere Aufgabe, standardisierte neue Schulen zu bauen und bestehende zu erweitern.\"]', '2015-06-27 08:35:43', '2015-06-22 08:04:35');
INSERT INTO `field_gallery_image` (`pages_id`, `data`, `sort`, `description`, `modified`, `created`) VALUES('1105', '4.jpg', '5', '[\"Fruchtbare Landschaft: Äthiopien im Besitz einer fruchtbaren Landschaft leidet unter Unwissen, Hunger und Krankheit, die zu beheben gilt. Hierfür sind geeignete Hilfe und unermüdliche Einsatz der eignen Bevölkerung gefragt. Unser Verein bemüht sich diese Umstände zu lindern, wenn auch im Kleinen Umfang.\"]', '2015-06-27 08:35:43', '2015-06-22 08:04:35');
INSERT INTO `field_gallery_image` (`pages_id`, `data`, `sort`, `description`, `modified`, `created`) VALUES('1105', '8.jpg', '4', '[\"\"]', '2015-06-22 08:04:35', '2015-06-22 08:04:35');
