INSERT INTO `field_gallery_image` (`pages_id`, `data`, `sort`, `description`, `modified`, `created`) VALUES('1105', '2.jpg', '1', '[\"Beratung und Einbeziehung: Beratung und Einbeziehung der Dorfgemeinschaft ist unabdingbar für eine nachhaltige Hilfe. Nur wenn sie sich in den Projekten, sei es durch Eigenarbeit, lokale Materialbeitrag (z. B. Steine, Bauholz, etc.), kleine Geldspende beteiligen, identifizieren sich mit dem Vorhaben und dessen Endergebnis.\"]', '2015-06-27 08:35:43', '2015-06-22 08:04:35');
INSERT INTO `field_gallery_image` (`pages_id`, `data`, `sort`, `description`, `modified`, `created`) VALUES('1105', '5.jpg', '2', '[\"\"]', '2015-06-22 08:04:35', '2015-06-22 08:04:35');
INSERT INTO `field_gallery_image` (`pages_id`, `data`, `sort`, `description`, `modified`, `created`) VALUES('1105', 'bild0422.jpg', '3', '[\"Frauen im Mittelpunkt: Vor Beginn aller Projekte sind Frauen ermutigt ihre Ideen einzubringen. Sie leiden unter häusliche Gewalt, sie tragen die meiste Last, um z. B. Wasser und Brennholz zu holen. Deshalb hat die Einbindung der Frauen in allen Aktivitäten höchste Priorität.\"]', '2015-06-27 08:35:43', '2015-06-27 08:12:30');
