INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('4959', 'to_use_unix_zip_unzip_command_specify_access_path_to_command_warning_php_configuration_may_forbid_this_solution', 'Utilisez les commandes unix zip et unzip. Pour cela, veuillez pr&eacute;ciser les chemins d\'acc&egrave;s vers ces commandes. Attention! Cette solution ne fonctionne pas quand php est en safe-mode ou quand la commande php <code>system</code> est d&eacute;sactiv&eacute;e, ce qui est le cas chez la plupart des h&eacute;bergeurs.', 'fr', 'install', '2', '2005-05-29 21:50:05');
