INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5423', 'howto_automatic_account_on_bugs_when_error_dont_appear_on_screen', 'Lodel can send an email when it encounters certain bugs for which it cannot display an error message on screen (as is the case when an unidentified internet user produces an error on the site). Leave empty if you do not wish to send mail.', 'en', 'install', '2', '2005-05-10 22:51:00');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5424', 'howto_configure_servoo_and_howto_get_an_account', '<p style=\"text-align:justify;\">The conversion of documents formatted as RTF, SXW ou DOC into XHTML is handled by a remote server; using OpenOffice.org and Writer2Latex (ServOO).</p><p style=\"text-align:justify;\">To obtain an account on a public ServOO, please consult the official ServOO listing on <a href=\"http://www.lodel.org\">http://www.lodel.org</a>. While waiting to receive your identifier, you can choose not to configure ServOO.</p><p style=\"text-align:justify;\">Use of this powerful feature is optional and can be configured later.</p>', 'en', 'admin', '2', '2009-06-09 15:13:57');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5425', 'howto_configure_to_ease_installation', 'The configuration options allow for simplified installation appropriate to the requirements of a particular host. If there is no specific configuration for your host, choose the default configuration.', 'en', 'install', '2', '2005-05-13 16:13:31');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5426', 'howto_import_database', 'To import a database, two options', 'en', 'admin', '2', '2005-04-28 22:31:38');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5427', 'howto_import_huge_files_and_database', 'When the database and associated files reach a significant size, it is not possible to import files via a simple web form. In such circumstances, backup files must be put into the import directory (e.g. by ftp). If, additionally, the webserver can write to this directory, Lodel will offer to store backups there (backup.php). This directory is, therefore, very useful for large sites.', 'en', 'install', '2', '2005-05-13 17:22:37');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5428', 'howto_index_type_1', 'Tag and style must contain only non-accented letters and numbers', 'en', 'admin', '2', '2005-04-28 22:31:38');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5429', 'howto_index_type_2', 'The style defined in the text-processing program\'s Document Model may contain accented characters and spaces. Accents and spaces will be automatically removed. For example, the style \"Keyword\" in the Document Model, must be defined as \"Keyword\" here', 'en', 'admin', '2', '2005-04-29 10:29:31');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5430', 'howto_index_type_3', '\"Style\" may also indicate language. If this is the case, each style must be followed by \":\" and the language code. Furthermore, the styles must be separated by a comma. Example: motcle:fr, keywords:en.', 'en', 'admin', '2', '2005-04-29 10:29:31');
