INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5434', 'howto_install_servoo_on_private_server', 'You can install your own ServOO server : this requires installation of OpenOffice.org and Java on your server. Such an installation requires a dedicated server securely set up. ServOO is (will be) distributed in a separate package from Lodel from <a href=\"http://sourceforge.net/projects/lodel/\" target=\"_new\">http://sourceforge.net/projects/lodel/</a>. When ServOO is installed, enter teh connection parameters below. If you are installing a ServOO server, we would encourage you to make it publicly available and to register it at <a href=\"http://www.lodel.org/\" target=\"_new\">http://www.lodel.org</a>.', 'en', 'install', '2', '2005-05-13 15:49:51');
