INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5439', 'howto_on_scripts_extensions_choose_php_or_html_extensions', 'Lodel scripts are PHP scripts, and it is recommended that you use the extension \".php\". However, if you wish, it is possible to choose the extension \".html\". This option is not recommended : it requires  in particular special configuration of the server (i.e. that html files be parsed by php).', 'en', 'install', '2', '2005-05-13 15:44:21');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5440', 'howto_parameters_mysql_create_database', 'Please check that the database access rights are set correctly, that MySQL user %s is entitled to create a new database (the majority of providers do not allow this) or begin the installation process afresh.', 'en', 'lodeladmin', '2', '2005-04-21 18:10:31');
INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('5441', 'howto_symbolic_links_in_lodel', 'If your webserver supports symbolic links, certain files can be linked rather than copied. Linking allows more flexible management of updates, access rights, etc. This is only warranted <strong>if Lodel is managing several sites</strong>. It is necessary, if that is not done, to configure the webserver to \"follow symbolic links\" which is generally considered less secure. Please consult your webserver\'s software documentation (e.g., Apache).', 'en', 'install', '2', '2005-05-13 15:27:55');
