INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('6216', 'howto_install_servoo_on_private_server', 'Vous pouvez installer vous propre ServOO : cela nécessite l\'installation de OpenOffice.org et Java sur votre serveur. Cette installation requiert un serveur dedié et correctement protégé. ServOO est (sera) distribué dans un paquetage séparé de Lodel sur <a href=\"http://sourceforge.net/projects/lodel/\" target=\"_new\">http://sourceforge.net/projects/lodel/</a>. Lorsque ServOO est installé, entrez les paramètres de connexion ci-dessous. Si vous installez un ServOO, nous vous encourageons à le mettre à disposition du public et à vous faire enregistrer sur <a href=\"http://www.lodel.org/\" target=\"_new\">http://www.lodel.org</a>.', 'ar', 'install', '-1', '2005-04-28 15:18:08');
