INSERT INTO `lodel_texts` (`id`, `name`, `contents`, `lang`, `textgroup`, `status`, `upd`) VALUES('7147', 'howto_on_scripts_extensions_choose_php_or_html_extensions', 'Les scripts Lodel sont des scripts PHP, et il est recommand&eacute; d\'utiliser l\'extension \".php\". Cependant si vous le souhaitez, il est possible de choisir l\'extension \".html\". Cette option n\'est pas recommand&eacute;e : elle n&eacute;cessite en particulier une configuration particuli&egrave;re sur le serveur (c\'est à dire que les fichiers html soient analys&eacute;s par php).', 'es', 'install', '-1', '2005-09-05 08:42:58');
